mapper.update:
update
	tb_shopping_basket
set
	stock = ${sbStock}
where
	member_id = #{sbMemberId} 
	and
	ingredient_no = #{sbIngNo}

mapper.delete:
delete from
	tb_shopping_basket
where
	member_id = #{sbMemberId}
	and
	ingredient_no = #{sbIngNo}

mapper.insert:
{call declare begin
insert into tb_ingredieont values( seq_Ingredient.nextval(), #{ingPrCategory}, #{ingPrCategory}, #{ingredientName}, #{ingfileName}, #{engPrCategory}, #{engPrCategory} );
insert into tb_ingredient_mall values( seq_ingredient.currval(), #{price}, #{stock}, #{minUnit}, #{ingOrigin}, #{shelfLife}, #{ingInfo} );
commit; end }
