mapper.delete:
delete from tb_like 
where 
	member_id = #{memberId} 
	and recipe_no = #{recipeNo}

delete from TB_scrap 
where 
	member_id = #{scrapId}
	and 
	recipe_no= #{recipeNo}

mapper.select:
SELECT J.INCLUDE_NO, C.CHEF_PROFILE, R.* FROM TB_RECIPE R JOIN (SELECT COUNT(*) INCLUDE_NO, T.RECIPE_NO FROM (SELECT * FROM TB_REC_INGREDIENTS WHERE INGREDIENT_NO IN
#{item}
) T GROUP BY RECIPE_NO ORDER BY 1) J ON R.RECIPE_NO = J.RECIPE_NO LEFT JOIN TB_CHEF C ON R.CHEF_ID = C.MEMBER_ID

	SELECT * FROM 
		(SELECT ROWNUM RNUM, I.* FROM TB_INGREDIENT I RIGHT JOIN 
				(SELECT COUNT(*) CNT, INGREDIENT_NO FROM TB_REC_INGREDIENTS WHERE INGREDIENT_NO IS NOT NULL  GROUP BY INGREDIENT_NO  ORDER BY CNT DESC) P
                    ON I.INGREDIENT_NO = P.INGREDIENT_NO


	                    AND ING_CD_CATEGORY = #{subCtg}  


	                    AND ING_PR_CATEGORY = #{mainCtg}  


                    )
		WHERE RNUM < 13
